import React from 'react'
import HeaderOptions from './headerOptions.js'
import Logo from './logo.js'
// import ConnectedGoals from './Goals.js'
import { connect } from 'react-redux'
// import {
//   handleInitialData
// } from '../actions/shared'

class App extends React.Component {
  componentDidMount () {
    const { dispatch } = this.props

    // dispatch(handleInitialData())
  }
  render() {
    if (this.props.loading === true) {
      return <h3>Loading</h3>
    }

    return (
      <div>
        <div className="parallax">
          <div className="flexMeMain">
            <div className="flexMeMainDiv">
              <Logo></Logo>
            </div>
            <div className="flexMeMainDiv">
              <HeaderOptions></HeaderOptions>
            </div>
            <div className="flexMeMainDiv right">
              <button className="homeSecondaryBtn">WATCH NOW</button>
              <button className="homeSecondaryBtn">GIVE</button>
            </div>
          </div>
          <div className="AbsoluteCenterDiv">
            <button className="HomeWatchNowButton">
              <a target="_blank" href="https://www.youtube.com/watch?v=0exFhX_hxIo" >CLICK TO WATCH</a>
            </button>
          </div>
        </div>
        <div className="stuff">
        Scroll Up and Down this page to see the parallax scrolling effect.
        This div is just here to enable scrolling.
        Tip: Try to remove the background-attachment property to remove the scrolling effect.
        </div>

      </div>
    )
  }
}

export default connect((state) => ({
  loading: state.loading
}))(App)
